import React, { useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';
import CardRenderer from './components/CardRenderer'; // Importation du composant CardRenderer

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const CardsContainer = styled.div`
  opacity: ${({ hasEntered }) => (hasEntered ? 1 : 0)};
  transform: translateY(50px);
  transition: opacity 1s ease, transform 1s ease;
`;

const MainComponentContent = () => {
  const { selectedCard } = useCard();  // Utilisation du hook pour obtenir la carte sélectionnée
  const [animateLogo, setAnimateLogo] = React.useState(false);
  const [backgroundColor, setBackgroundColor] = React.useState('#FFFFFF'); 
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 1,
            ease: 'power2.out',
            delay: 0.2,
          });
        }
      },
    });
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      {/* Afficher les cartes si aucune carte n'est sélectionnée */}
      {!selectedCard && (
        <CardsContainer ref={cardsRef}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {/* Afficher le composant correspondant à la carte sélectionnée */}
      {selectedCard && <CardRenderer />}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

const MainComponent = () => {
  return (
    <CardProvider>
      <PageProvider>
        <MainComponentContent />
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
