import React, { useState, useEffect } from 'react';
import LottieAnimation from './LottieAnimation';
import Card1 from './Card1';
import Card2 from './Card2';
import Card3 from './Card3';
import { useCard } from './CardContext';

const TransitionPage = ({ onComplete, onBack }) => {
  const { selectedCard, isLottieVisible, finishAnimation } = useCard();
  const [showNewContent, setShowNewContent] = useState(false);

  useEffect(() => {
    if (!isLottieVisible && selectedCard) {
      const timer = setTimeout(() => {
        setShowNewContent(true);
      }, 1000); // Délai de 1 seconde après la fin de l'animation

      return () => clearTimeout(timer);
    }
  }, [isLottieVisible, selectedCard]);

  const getLottieData = () => {
    switch (selectedCard) {
      case 'card1':
        return require('../path/to/lottiefile1.json'); // Chemin correct vers le fichier Lottie
      case 'card2':
        return require('../path/to/lottiefile2.json'); // Chemin correct vers le fichier Lottie
      case 'card3':
        return require('../path/to/lottiefile3.json'); // Chemin correct vers le fichier Lottie
      default:
        return null;
    }
  };

  const getNewContent = () => {
    if (!showNewContent) return null;

    switch (selectedCard) {
      case 'card1':
        return <Card1 />;
      case 'card2':
        return <Card2 />;
      case 'card3':
        return <Card3 />;
      default:
        return null;
    }
  };

  return (
    <div>
      {isLottieVisible && getLottieData() && (
        <LottieAnimation
          animationData={getLottieData()}
          onComplete={finishAnimation}
        />
      )}
      {!isLottieVisible && showNewContent && (
        <div>
          {getNewContent()}
          <button onClick={onBack}>Back</button>
        </div>
      )}
    </div>
  );
};

export default TransitionPage;
