import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled, { keyframes } from 'styled-components';

// Animation de fondu en sortie
const fadeOut = keyframes`
  from {
    opacity: 1;
  }
  to {
    opacity: 0;
  }
`;

const BackgroundContainer = styled.div`
  position: relative;
  background-color: #fff; /* Fond blanc */
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: #fff;
  z-index: 100000002; /* Couvre le contenu */
  animation: ${({ animate }) => (animate ? `${fadeOut} 1s forwards` : 'none')};
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%; 
  pointer-events: all;
`;

const MainComponent = () => {
  const [loading, setLoading] = useState(true);
  const [animateLogo, setAnimateLogo] = useState(false);
  const [fadeOutBackground, setFadeOutBackground] = useState(false);

  const handleEnter = () => {
    setAnimateLogo(true);  // Déclenche l'animation du logo
    setFadeOutBackground(true);  // Déclenche l'animation de fondu du fond blanc

    // Retirer le LoadingScreen après un délai
    setTimeout(() => {
      setLoading(false);
    }, 1000); // Correspond à la durée de l'animation
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer>
          <Logo animate={animateLogo} />
          {loading && (
            <>
              <WhiteOverlay animate={fadeOutBackground} />
              <OverlayContainer>
                <ButtonContainer>
                  <button onClick={handleEnter}>Entrer</button>
                </ButtonContainer>
              </OverlayContainer>
            </>
          )}
          {!loading && (
            <div>
              <Navbar />
              <CardsWrapper />
            </div>
          )}
          <GrainEffect /> {/* Positionné au-dessus du fond blanc */}
        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
