import React, { useState, useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  z-index: 100000002;
  background-color: #fff;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
`;

const GrainOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  pointer-events: none;
  z-index: 100000004;
`;

const CardsContainer = styled.div`
  opacity: 0; /* Cacher les cartes au départ */
  transform: translateY(50px); /* Décale les cartes vers le bas */
  z-index: 100000005; /* Assurez-vous que les cartes sont au-dessus du GrainEffect */
  position: relative; /* Position relative pour être affecté par le z-index */
`;

const MainComponent = () => {
  const [animateLogo, setAnimateLogo] = useState(false);
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    // Utiliser GSAP pour animer la disparition du fond blanc et du bouton simultanément
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        overlayRef.current.style.display = 'none'; // Cache complètement le fond blanc après l'animation

        // Animation des cartes
        gsap.to(cardsRef.current, {
          opacity: 1,
          y: 0,
          duration: 1,
          ease: 'power2.out',
          delay: 0.2, // Délai pour synchroniser avec la disparition du fond blanc
        });
      }
    });
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer>
          <Logo animate={animateLogo} />
          <Navbar />

          {/* Cartes cachées au départ */}
          <CardsContainer ref={cardsRef}>
            <CardsWrapper />
          </CardsContainer>

          {/* Grain effect déjà chargé */}
          <GrainEffect /> 

          {/* Couche de superposition blanche qui sera animée pour disparaître */}
          <WhiteOverlay ref={overlayRef} />

          {/* Overlay pour le bouton */}
          <OverlayContainer>
            <ButtonContainer ref={buttonRef}>
              <button onClick={handleEnter}>Entrer</button>
            </ButtonContainer>
          </OverlayContainer>

        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
