import React, { useState } from 'react';
import Button from './navbutton';
import Modal from '../Modal';
import { useCard } from '../CardContext'; // Importation du contexte
import { FaKey, FaEye, FaUser } from 'react-icons/fa';

const NavbarRight = () => {
  const [isModalOpen, setModalOpen] = useState(false);
  const [selectedToys, setSelectedToys] = useState([]);
  const [formData, setFormData] = useState({ firstName1: '', firstName2: '' });
  const { selectedCard, resetCard } = useCard(); // Accès au contexte pour réinitialiser l'état

  const openModal = () => {
    console.log("Modal is opening...");
    setModalOpen(true);
  };

  const closeModal = () => {
    console.log("Modal is closing...");
    setModalOpen(false);
  };

  const handleSave = (data) => {
    console.log("Saving data from modal:", data);
    setSelectedToys(data.selectedToys);
    setFormData({
      firstName1: data.firstName1,
      firstName2: data.firstName2,
    });
    closeModal();
  };

  const handleReturn = () => {
    console.log("Returning to initial state. Hiding the card...");
    resetCard(); // Réinitialiser l'état des cartes pour cacher la carte sélectionnée
    console.log("Card has been reset.");
  };

  console.log("Current selectedCard:", selectedCard);

  return (
    <div style={{ display: 'flex', flexDirection: 'row', position: 'absolute', right: '48px', top: '48px', gap: '10px' }}>
      <Button icon={<FaUser />} label="Filters" onClick={openModal} />
      {/* Afficher le bouton "Retour" si une carte est sélectionnée */}
      {selectedCard && (
        <Button label="Retour" onClick={handleReturn} />
      )}
      <Modal 
        isOpen={isModalOpen} 
        onClose={closeModal} 
        onSave={handleSave} 
        selectedToys={selectedToys}
        formData={formData}
        setFormData={setFormData}
      />
    </div>
  );
};

export default NavbarRight;
