import React, { useState, useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import Logo from './components/Logo';
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor}; /* Applique la couleur de fond dynamiquement */
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none; /* Désactive les interactions une fois invisible */
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none; /* Désactive les interactions une fois invisible */
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto; /* Assure que le bouton est cliquable */
`;

const CardsContainer = styled.div`
  opacity: 0;
  transform: translateY(50px);
  transition: opacity 0.5s ease, transform 0.5s ease;
`;

const MainComponent = () => {
  const [animateLogo, setAnimateLogo] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF'); // Couleur de fond par défaut
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    // Utiliser GSAP pour animer la disparition du fond blanc et du bouton simultanément
    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none'; // Désactive les interactions
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none'; // Désactive les interactions du bouton après l'animation
        }

        // Animation des cartes
        if (cardsRef.current) {
          gsap.to(cardsRef.current, {
            opacity: 1,
            y: 0,
            duration: 1,
            ease: 'power2.out',
            delay: 0.2,
          });
        }
      }
    });
  };

  return (
    <CardProvider>
      <PageProvider>
        <BackgroundContainer bgColor={backgroundColor}>
          <Logo animate={animateLogo} />
          <Navbar />

          <CardsContainer ref={cardsRef}>
            <CardsWrapper setBackgroundColor={setBackgroundColor} /> {/* Passer la fonction au composant */}
          </CardsContainer>

          <WhiteOverlay ref={overlayRef} />

          <OverlayContainer>
            <ButtonContainer ref={buttonRef}>
              <button onClick={handleEnter}>Entrer</button>
            </ButtonContainer>
          </OverlayContainer>

        </BackgroundContainer>
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
