import React, { useState } from 'react';
import Navbar from './components/navbar/navbar';
import CardTransitionPage from './components/CardTransitionPage';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext';
import { PageProvider } from './components/PageContext';
import GrainEffect from './components/GrainEffect';
import LoadingScreen from './components/LoadingScreen';
import styled from 'styled-components';
import Logo from './images/logo.svg'; // Assurez-vous que le chemin est correct

const MainContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  background-color: #faf3d9;
`;

const LogoInMain = styled.img`
  position: absolute;
  width: 100px;
  top: 20px;
  left: 20px;
`;

const MainContent = () => {
  const { selectedCard, isLottieVisible, finishAnimation, resetCard } = useCard();

  return (
    <div>
      <Navbar />  {/* Navbar sans logo */}

      {!selectedCard && (
        <CardsWrapper setBackgroundColor={(color) => document.body.style.backgroundColor = color} />
      )}

      {selectedCard && (
        <CardTransitionPage
          selectedCard={selectedCard}
          onComplete={finishAnimation}
          onBack={resetCard}
        />
      )}
    </div>
  );
};

const MainComponent = () => {
  const [loading, setLoading] = useState(true);

  const handleEnter = () => {
    setLoading(false);
  };

  if (loading) {
    return <LoadingScreen onEnter={handleEnter} />;
  }

  return (
    <CardProvider>
      <PageProvider>
        <MainContainer>
          <LogoInMain src={Logo} alt="Logo" />
          <MainContent />
        </MainContainer>
      </PageProvider>
      <GrainEffect />
    </CardProvider>
  );
};

export default MainComponent;
