import React, { useEffect, useState, useContext } from 'react';
import styled from 'styled-components';
import { UserContext } from './UserContext'; // Import the context

// Styled components for chips and layout
const ChipContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-bottom: 16px;
`;

const Chip = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#f0f0f0'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  border-radius: 20px;
  padding: 8px 16px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
  }
`;

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const ButtonContainer = styled.div`
  margin-top: 16px;
  display: flex;
  flex-direction: column;
  align-items: end;
`;

const CloseButton = styled.button`
  position: absolute;
  top: 40px;
  right: 40px;
  background: none;
  border: none;
  font-size: 40px;
  cursor: pointer;
  color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 32px;
  margin-right: 64px;
  line-height: 50px;
`;

const Modal = ({ isOpen, onClose, onSave }) => {
  const { selectedToys, updateUserPreferences } = useContext(UserContext);

  const [formState, setFormState] = useState({
    selectedToys: selectedToys || [],
    selectedCategories: [], // Track selected categories
  });

  const [toysByCategory, setToysByCategory] = useState({});

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await fetch('http://localhost:1812/api/toys');
        const toys = await response.json();

        // Organize toys by category
        const groupedToys = toys.reduce((acc, toy) => {
          const category = toy.category || 'Uncategorized'; // Default to 'Uncategorized' if no category
          if (!acc[category]) {
            acc[category] = [];
          }
          acc[category].push(toy);
          return acc;
        }, {});

        setToysByCategory(groupedToys);
      } catch (error) {
        console.error('Error fetching toys:', error);
      }
    };

    fetchToys();
  }, []);

  const toggleCategorySelection = (category) => {
    setFormState((prev) => ({
      ...prev,
      selectedCategories: prev.selectedCategories.includes(category)
        ? prev.selectedCategories.filter(c => c !== category)
        : [...prev.selectedCategories, category],
    }));
  };

  const toggleToySelection = (toy) => {
    setFormState((prev) => ({
      ...prev,
      selectedToys: prev.selectedToys.includes(toy.name)
        ? prev.selectedToys.filter(t => t !== toy.name)
        : [...prev.selectedToys, toy.name],
    }));
  };

  const handleSave = () => {
    updateUserPreferences(formState);
    onSave(formState);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Personnaliser votre expérience</SectionTitle>

        {/* Display Category Chips */}
        <ChipContainer>
          {Object.keys(toysByCategory).map((category, index) => (
            <Chip
              key={index}
              isSelected={formState.selectedCategories.includes(category)}
              onClick={() => toggleCategorySelection(category)}
            >
              {category}
            </Chip>
          ))}
        </ChipContainer>

        {/* Display Toys Chips when a Category is selected */}
        {formState.selectedCategories.map((category, index) => (
          <div key={index}>
            <ChipContainer>
              {toysByCategory[category].map((toy, toyIndex) => (
                <Chip
                  key={toyIndex}
                  isSelected={formState.selectedToys.includes(toy.name)}
                  onClick={() => toggleToySelection(toy)}
                >
                  {toy.name}
                </Chip>
              ))}
            </ChipContainer>
          </div>
        ))}

        <ButtonContainer>
          <button onClick={handleSave}>
            Confirmer 
          </button>
        </ButtonContainer>
      </ModalContent>
    </ModalOverlay>
  );
};x

export default Modal;
