import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 100%;
  width: 100%;
  padding: 20px;
`;

const Card = styled.div`
  width: 392px;
  height: 548px;
  background-color: #FBF8F1;
  border-radius: 16px;
  padding: 48px;
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  cursor: pointer;
  text-align: center;
`;

const Title = styled.h1`
  font-family: 'Poppins', sans-serif;
  font-size: 16px;
  font-weight: 700;
  color: #FFD700;
  margin-bottom: 20px;
  text-transform: uppercase;
`;

const Content = styled.p`
font-family: Paragon;
  font-size: 20px;
  font-weight: 400;
  color: #000000;
  margin-bottom: 30px;
`;

const Footer = styled.div`
  font-family: 'Poppins', sans-serif;
  text-transform: uppercase;
  font-size: 12px;
  font-weight: 400;
  color: #000000;
  margin-top: 20px;
`;

const ButtonContainer = styled.div`
  position:absolute;
  bottom : 48px;
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  display: flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background-color: white;
  border: 1px solid rgba(0, 0, 0, 0.12);
  border-radius: 12px;
  color: #000;
  font-family: 'Poppins', sans-serif;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: rotate(7deg) scale(1.05);
    background-color: #fff5f5;
    color: #ff4500;
    border-color: #ff4500;
    box-shadow: 0 6px 12px rgba(255, 69, 0, 0.3);
  }
`;

const Roleplay = () => {
  const [roleplay, setRoleplay] = useState(null);

  const fetchRandomRoleplay = async () => {
    try {
      const response = await axios.get('http://localhost:1812/api/roleplay/random');
      setRoleplay(response.data);
    } catch (error) {
      console.error('Erreur lors de la rÃ©cupÃ©ration du roleplay:', error);
    }
  };

  useEffect(() => {
    fetchRandomRoleplay(); // Charger un roleplay alÃ©atoire au chargement du composant
  }, []);

  if (!roleplay) return <div>Loading...</div>;

  return (
    <PageContainer>
      <Card>
        <Title>{roleplay.title}</Title>
        <Content>{roleplay.description}</Content>
        <Footer>LoveTogether</Footer>
      </Card>

      <ButtonContainer>
        <Button onClick={fetchRandomRoleplay}>
          <span role="img" aria-label="Another one">ðŸ”„</span> Another one
        </Button>
      </ButtonContainer>
    </PageContainer>
  );
};

export default Roleplay;
