import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const ModalOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: #fff;
  border-radius: 20px;
  padding: 48px;
  width: 80%;
  max-width: 800px;
  position: relative;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
`;

const CloseButton = styled.button`
position: absolute;
top: 40px;
right: 40px;
background: none;
border: none;
font-size: 40px;
cursor: pointer;
color: #000;
`;

const SectionTitle = styled.h2`
  font-size: 46px;
  font-weight: bold;
  color: #000;
  margin-bottom: 20px;
`;

const SubTitle = styled.h3`
  font-size: 20px;
  font-weight: bold;
  color: #333;
  margin-bottom: 16px;
`;

const ToyGrid = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
`;

const ToyButton = styled.button`
  background: ${props => props.isSelected ? '#ff4500' : '#fff'};
  border: 1px solid ${props => props.isSelected ? '#ff4500' : '#ddd'};
  color: ${props => props.isSelected ? '#fff' : '#000'};
  border-radius: 20px;
  padding: 10px 20px;
  cursor: pointer;
  transition: background-color 0.3s ease, color 0.3s ease, border-color 0.3s ease;

  &:hover {
    background: #ff4500;
    color: #fff;
    border-color: #ff4500;
  }
`;

const Modal = ({ isOpen, onClose }) => {
  const [toys, setToys] = useState([]);
  const [selectedToys, setSelectedToys] = useState([]);

  useEffect(() => {
    const fetchToys = async () => {
      try {
        const response = await axios.get('http://localhost:1812/api/toys'); // Remplacez par l'URL correcte de votre API
        setToys(response.data);
      } catch (error) {
        console.error('Erreur lors du chargement des toys:', error);
      }
    };

    fetchToys();
  }, []);

  const toggleToySelection = (toy) => {
    setSelectedToys(prevSelectedToys =>
      prevSelectedToys.includes(toy)
        ? prevSelectedToys.filter(t => t !== toy)
        : [...prevSelectedToys, toy]
    );


  };



  if (!isOpen) return null;

  return (
    <ModalOverlay>
      <ModalContent>
        <CloseButton onClick={onClose}>&times;</CloseButton>
        <SectionTitle>Modify your experience</SectionTitle>

        <SubTitle>Select your place</SubTitle>
        <PlaceGrid>
          {places.map((place, index) => (
            <PlaceButton
              key={index}
              isSelected={selectedPlace === place.name}
              onClick={() => selectPlace(place.name)}
            >
              <IconWrapper>{place.icon}</IconWrapper>
              <PlaceLabel>{place.name}</PlaceLabel>
            </PlaceButton>
          ))}
        </PlaceGrid>

        {/* Vous pouvez ajouter la s√©lection des jouets ici */}
      </ModalContent>
    </ModalOverlay>
  );
};

export default Modal;
