import React, { useState, useRef } from 'react';
import Navbar from './components/navbar/navbar';
import CardsWrapper from './components/CardsWrapper';
import { CardProvider, useCard } from './components/CardContext'; // Assurez-vous que CardProvider et useCard sont bien importés
import { PageProvider } from './components/PageContext';
import Logo from './components/Logo';
import ActionVerite from './components/ActionVerite'; // Assurez-vous d'importer ActionVerite
import styled from 'styled-components';
import { gsap } from 'gsap';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor};
`;

const WhiteOverlay = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  background-color: white;
  z-index: 100000002;
  pointer-events: none;
`;

const OverlayContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 100000003;
  pointer-events: none;
`;

const ButtonContainer = styled.div`
  position: absolute;
  top: 70%;
  pointer-events: auto;
`;

const CardsContainer = styled.div`
  opacity: 1; // Définit l'opacité par défaut à 1 pour s'assurer que les cartes sont visibles
  transform: translateY(50px);
  transition: opacity 0.5s ease, transform 0.5s ease;
`;

const MainComponentContent = () => {
  const { selectedCard } = useCard(); // Utiliser le hook useCard pour accéder à selectedCard
  const [animateLogo, setAnimateLogo] = useState(false);
  const [backgroundColor, setBackgroundColor] = useState('#FFFFFF'); // Couleur de fond par défaut
  const overlayRef = useRef(null);
  const buttonRef = useRef(null);
  const cardsRef = useRef(null);

  const handleEnter = () => {
    setAnimateLogo(true);

    gsap.to([overlayRef.current, buttonRef.current], {
      duration: 1,
      opacity: 0,
      ease: 'power2.out',
      onComplete: () => {
        if (overlayRef.current) {
          overlayRef.current.style.display = 'none';
          overlayRef.current.style.pointerEvents = 'none';
        }

        if (buttonRef.current) {
          buttonRef.current.style.pointerEvents = 'none';
        }

        if (cardsRef.current) {
          console.log("Avant animation:", cardsRef.current.style.opacity);
          gsap.to(cardsRef.current, {
            opacity: 1, // S'assurer que l'opacité est bien à 1 après l'animation
            y: 0,
            duration: 1,
            ease: 'power2.out',
            delay: 0.2,
            onComplete: () => {
              console.log("Après animation:", cardsRef.current.style.opacity);
            },
          });
        }
      },
    });
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <Logo animate={animateLogo} />
      <Navbar />

      {/* Affichage conditionnel basé sur l'état de selectedCard */}
      {selectedCard ? (
        <ActionVerite card={selectedCard} />
      ) : (
        <CardsContainer ref={cardsRef}>
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      <WhiteOverlay ref={overlayRef} />

      <OverlayContainer>
        <ButtonContainer ref={buttonRef}>
          <button onClick={handleEnter}>Entrer</button>
        </ButtonContainer>
      </OverlayContainer>
    </BackgroundContainer>
  );
};

const MainComponent = () => {
  return (
    <CardProvider>
      <PageProvider>
        <MainComponentContent />
      </PageProvider>
    </CardProvider>
  );
};

export default MainComponent;
