import React, { useEffect } from 'react';
import HomeCard from './HomeCard';
import { useCard } from './CardContext';
import LottieAnimation from './LottieAnimation';

const CardsWrapper = ({ setBackgroundColor }) => {
  const { selectCard, isLottieVisible, finishAnimation } = useCard();

  const handleCardClick = (card) => {
    console.log('handleCardClick called with:', card);
    selectCard(card);
  };

  useEffect(() => {
    console.log('isLottieVisible changed:', isLottieVisible);
  }, [isLottieVisible]);

  if (isLottieVisible) {
    console.log('LottieAnimation is visible');
    return (
      <LottieAnimation onComplete={finishAnimation} />
    );
  }

  console.log('Rendering HomeCards');
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'row',
      width: '100vw',
      height: '100vh',
      justifyContent: 'center',
      alignItems: 'center',
      gap: '20px',
      padding: '50px'
    }}>
      <HomeCard
        image={require('../images/actionVerite.png')}
        rotate="-5deg"
        onMouseEnter={() => {
          console.log('Mouse entered actionVerite card');
          setBackgroundColor('#FFD700');
        }}
        onMouseLeave={() => {
          console.log('Mouse left actionVerite card');
          setBackgroundColor('#FBF8F1');
        }}
        onClick={() => {
          console.log('actionVerite card clicked');
          handleCardClick('actionVerite');
        }}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(-5deg)',
          backgroundColor: '#FBF8F1',
        }}
      />
      <HomeCard
        image={require('../images/generator.png')}
        rotate="0deg"
        onMouseEnter={() => {
          console.log('Mouse entered generator card');
          setBackgroundColor('#FF69B4');
        }}
        onMouseLeave={() => {
          console.log('Mouse left generator card');
          setBackgroundColor('#FBF8F1');
        }}
        onClick={() => {
          console.log('generator card clicked');
          handleCardClick('generator');
        }}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(0deg)',
          backgroundColor: '#FBF8F1',
        }}
      />
      <HomeCard
        image={require('../images/roleplay.png')}
        rotate="5deg"
        onMouseEnter={() => {
          console.log('Mouse entered roleplay card');
          setBackgroundColor('#FF4500');
        }}
        onMouseLeave={() => {
          console.log('Mouse left roleplay card');
          setBackgroundColor('#FBF8F1');
        }}
        onClick={() => {
          console.log('roleplay card clicked');
          handleCardClick('roleplay');
        }}
        style={{
          width: '30vw',
          height: '40vh',
          transform: 'rotate(5deg)',
          backgroundColor: '#FBF8F1',
        }}
      />
    </div>
  );
};

export default CardsWrapper;
