import React, { useRef, useState, useEffect } from 'react';
import styled from 'styled-components';
import Navbar from './navbar/navbar';
import CardsWrapper from './CardsWrapper';
import { useCard } from './CardContext';
import NavbarRight from './navbar/navbarright';
import CardRenderer from './CardRenderer';
import gsap from 'gsap';
import Logo from './Logo';
import GrainEffect from './GrainEffect';

const BackgroundContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  background-color: ${({ bgColor }) => bgColor || '#FFFFFF'};  /* Par défaut, le fond est blanc */
`;

const LogoContainer = styled.div`
  position: absolute;
  top: 50px;
  left: 50%;
  transform: translateX(-50%) scale(0.2);
  transform-origin: center top;
  z-index: 100000;
`;

const CardsContainer = styled.div`
  opacity: ${({ opacity }) => opacity};
  transform: translateY(${({ isAnimated }) => (isAnimated ? '0' : '50px')});
  transition: ${({ isAnimated }) => (isAnimated ? 'transform 0.8s ease, opacity 0.8s ease' : 'none')};
  pointer-events: ${({ clickable }) => (clickable ? 'auto' : 'none')};
  z-index: 20;
`;

const GrainContainer = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
`;

const MainContent = ({ backgroundColor, setBackgroundColor }) => {
  const { selectedCard, resetCards } = useCard();
  const [cardsOpacity, setCardsOpacity] = useState(1);
  const [isAnimated, setIsAnimated] = useState(true);
  const [cardsClickable, setCardsClickable] = useState(true);
  const cardsRef = useRef(null);

  useEffect(() => {
    // Définir la couleur de fond à blanc au montage du composant
    setBackgroundColor('#FFFFFF');

    // Démarrer l'animation des cartes lors du montage du composant
    if (cardsRef.current) {
      gsap.to(cardsRef.current, {
        opacity: 1,
        y: 0,
        duration: 0.8,
        ease: 'power4.out',
        delay: 0.1,
        onComplete: () => {
          setCardsClickable(true);
        },
      });
    }
  }, [setBackgroundColor]);

  const handleXIconClick = () => {
    resetCards();
    setIsAnimated(false);
    setCardsOpacity(1);
    setCardsClickable(true);

    // Assurez-vous que l'opacité des cartes passe à 1 lorsque l'utilisateur clique sur XIcon
    if (cardsRef.current) {
      gsap.set(cardsRef.current, {
        opacity: 1,
      });
    }
  };

  return (
    <BackgroundContainer bgColor={backgroundColor}>
      <GrainContainer>
        <GrainEffect />
      </GrainContainer>

      <LogoContainer>
        <Logo />
      </LogoContainer>

      <Navbar />
      <NavbarRight onResetAnimation={handleXIconClick} />

      {!selectedCard && (
        <CardsContainer
          ref={cardsRef}
          opacity={cardsOpacity}
          isAnimated={isAnimated}
          clickable={cardsClickable}
        >
          <CardsWrapper setBackgroundColor={setBackgroundColor} />
        </CardsContainer>
      )}

      {selectedCard && <CardRenderer />}
    </BackgroundContainer>
  );
};

export default MainContent;
